import PropTypes from "prop-types"
import React, { Component } from "react"

//Simple bar
import SimpleBar from "simplebar-react"

// MetisMenu
import MetisMenu from "metismenujs"
import { withRouter } from "react-router-dom"
import { Link } from "react-router-dom"

//i18n
import { withTranslation } from "react-i18next"

class SidebarContent extends Component {
  constructor(props) {
    super(props)
    this.refDiv = React.createRef()
  }

  componentDidMount() {
    this.initMenu()
  }

  // eslint-disable-next-line no-unused-vars
  componentDidUpdate(prevProps, prevState, ss) {
    if (this.props.type !== prevProps.type) {
      this.initMenu()
    }
  }

  initMenu() {
    new MetisMenu("#side-menu")

    let matchingMenuItem = null
    const ul = document.getElementById("side-menu")
    const items = ul.getElementsByTagName("a")
    for (let i = 0; i < items.length; ++i) {
      if (this.props.location.pathname === items[i].pathname) {
        matchingMenuItem = items[i]
        break
      }
    }
    if (matchingMenuItem) {
      this.activateParentDropdown(matchingMenuItem)
    }
  }

  // componentDidUpdate() {}

  scrollElement = item => {
    setTimeout(() => {
      if (this.refDiv.current !== null) {
        if (item) {
          const currentPosition = item.offsetTop
          if (currentPosition > window.innerHeight) {
            if (this.refDiv.current)
              this.refDiv.current.getScrollElement().scrollTop =
                currentPosition - 300
          }
        }
      }
    }, 300)
  }

  activateParentDropdown = item => {
    item.classList.add("active")
    const parent = item.parentElement

    const parent2El = parent.childNodes[1]
    if (parent2El && parent2El.id !== "side-menu") {
      parent2El.classList.add("mm-show")
    }

    if (parent) {
      parent.classList.add("mm-active")
      const parent2 = parent.parentElement

      if (parent2) {
        parent2.classList.add("mm-show") // ul tag

        const parent3 = parent2.parentElement // li tag

        if (parent3) {
          parent3.classList.add("mm-active") // li
          parent3.childNodes[0].classList.add("mm-active") //a
          const parent4 = parent3.parentElement // ul
          if (parent4) {
            parent4.classList.add("mm-show") // ul
            const parent5 = parent4.parentElement
            if (parent5) {
              parent5.classList.add("mm-show") // li
              parent5.childNodes[0].classList.add("mm-active") // a tag
            }
          }
        }
      }
      this.scrollElement(item)
      return false
    }
    this.scrollElement(item)
    return false
  }

  render() {
    return (
      <React.Fragment>
        <SimpleBar className="h-100" ref={this.refDiv}>
          <div id="sidebar-menu">
            <ul className="metismenu list-unstyled" id="side-menu">
            <li>
                <Link to="#" className="">
                  <i className="bx bx-user-plus" />

                  <span>{this.props.t("Dashboard")}</span>
                </Link>
              </li>
            <li>
                <Link to="#" className="has-arrow">
                  <i className="bx bx-user-plus" />

                  <span>{this.props.t("Activities")}</span>
                </Link>
                <ul className="sub-menu" aria-expanded="false">
                  <li>
                  <Link to="#">{this.props.t("All Activities")}</Link>
                    <Link to="#">{this.props.t("Calendar")}</Link>
                  </li>

                </ul>
              </li>
              <li>
                <Link to="#" className="has-arrow">
                  <i className="bx bx-user-plus" />

                  <span>{this.props.t("Leads")}</span>
                </Link>
                <ul className="sub-menu" aria-expanded="false">
                  <li>
                    <Link to="/dashboard">{this.props.t("Dashboard")}</Link>
                  </li>
                  <li>
                    <Link to="#">{this.props.t("All Leads")}</Link>
                  </li>
                </ul>
              </li>
              <li>
                <Link to="#" className="">
                  <i className="bx bx-user-plus" />

                  <span>{this.props.t("Contacts")}</span>
                </Link>
              </li>
              <li>
                <Link to="#" className="">
                  <i className="bx bx-user-plus" />

                  <span>{this.props.t("Clients")}</span>
                </Link>
              </li>
              <li>
                <Link to="#" className="has-arrow">
                  <i className="bx bx-user-plus" />

                  <span>{this.props.t("Pre-Construction")}</span>
                </Link>
                <ul className="sub-menu" aria-expanded="false">
                  <li>
                    <Link to="/pre-construction/project">{this.props.t("Projects")}</Link>
                  </li>
                  <li>
                    <Link to="/pre-construction/builder">{this.props.t("Builders")}</Link>
                  </li>
                </ul>
              </li>
              <li>
                <Link to="#" className="">
                  <i className="bx bx-user-plus" />

                  <span>{this.props.t("Email Composer")}</span>
                </Link>
              </li>
              <li>
                <Link to="#" className="">
                  <i className="bx bx-user-plus" />

                  <span>{this.props.t("Settings")}</span>
                </Link>
              </li>
            </ul>
          </div>
        </SimpleBar>
      </React.Fragment>
    )
  }
}

SidebarContent.propTypes = {
  location: PropTypes.object,
  t: PropTypes.any,
  type: PropTypes.string,
}

export default withRouter(withTranslation()(SidebarContent))
